	@Test
	public void testHeuristicHUGE() throws FileNotFoundException {
		System.out.println("\n----- Heuristic scheduling: Huge instance -----\n");
		
		// Place huge.txt in the project folder inside the Eclipse workspace.
		
		ArrayList<Integer> tasks = new ArrayList<Integer>();
		Scanner scanner = new Scanner(new File("C:\\Users\\Yogisha\\workspace\\Scheduling\\src\\huge.txt"));
		while (scanner.hasNextLine()) {
			String line = scanner.nextLine();
			tasks.add(Integer.parseInt(line));
		}
		
		int n = tasks.size();
		int[] tasksHuge = new int[n];
		for (int i=0; i<n; i++) tasksHuge[i] = tasks.get(i);
		
		int mHuge = 10000;
		ScheduleGenerator s = new ScheduleGenerator(tasksHuge,mHuge);
		Schedule sched = s.heuristicScheduling();
		System.out.println("# tasks = " + n);
		System.out.println("# processors = " + mHuge);
		//System.out.println(sched);
		System.out.println("utilization = " + sched.getUtilization());
	}
	
	@Test
	public void testGetOptSchedule() {
		
		System.out.println("\n----- Optimal schedule: Small instance -----\n");
		
		int[] tasksSmall = new int[] { 7,5,2,4,6 };
		int mSmall = 2;
		ScheduleGenerator s = new ScheduleGenerator(tasksSmall,mSmall);
		Schedule sched = s.getOptSchedule();
		System.out.println(sched);
		assertEquals(12,sched.getMakespan());
		
		
		System.out.println("\n----- Optimal schedule: Medium instance -----\n");
		
		int[] tasksMedium = new int[] { 10, 23, 57, 98, 47, 23, 39, 55, 63, 12, 73 };
		int mMedium = 3;
		s = new ScheduleGenerator(tasksMedium,mMedium);
		sched = s.getOptSchedule();
		System.out.println(sched);
		assertEquals(167,sched.getMakespan());
		
		
		System.out.println("\n----- Optimal schedule: Big instance -----\n");
		
		int[] tasksBig = new int[] { 27, 16, 25, 48, 27, 33, 49, 35, 73, 102, 22, 99, 69, 49 };
		int mBig = 4;
		s = new ScheduleGenerator(tasksBig,mBig);
		sched = s.getOptSchedule();
		System.out.println(sched);
		assertEquals(169,sched.getMakespan());
	}

